                bfo:concretizes ?study_design.
         ?study_design bfo:has_part ?protocol .
         ?protocol bfo:has_part ?study_var_design.
         ?study_var_design a cdio:study_design_variable_specification;
                             ns1:has_part ?data_element.
    }
}

